Command:
list warehouses
|
[select @wh_id];
{
    list warehouses
    |
    process dss auto manifest carton on packout
     where ctnnum = 'SHP00005LPBI'
       and devcod = 'SOBCLP0939'
       and wh_id = @wh_id
       and weight = '60' catch(@?)
};
{
    [select mfsmsn
       from manfst
      where subnum = 'SHP00005LPBI'] catch(@?)
    |
    if (@? = 0)
    {
        list warehouses
        |
        remove pm package
         where mfsmsn = @mfsmsn
           and wh_id = @wh_id
    }
};
